import { useState, useEffect, useRef } from 'react';
import { Usuario, ReqRespUsers } from '../interfaces/reqResp';
import { reqRespApi } from '../api/reqRes';

export const useUsuarios = () => {
    const [usuarios, setUsuarios] = useState<Usuario[]>([])
    const [resp, setResp] = useState<Usuario[]>([])

    useEffect(() => {
        cargarUsuarios();
    }, [])

    const pagRef = useRef(0);

    const cargarUsuarios = async () => {
        const res = await reqRespApi.get<ReqRespUsers>('/users', {
            params: {
                page: pagRef.current
            }
        })
        setResp(res.data.data)

    }

    const pagSig = () => {
        if (resp.length > 0) {
            setUsuarios(resp);
            pagRef.current++;
        } else {
            alert("No hay mas registros")
        }
        setUsuarios(resp)
        console.log(pagRef.current)

    }

    const pagAnt = () => {
        if (resp.length > 0) {
            setUsuarios(resp);
            pagRef.current--;
        } else {
            alert("No hay mas registros")
        }
        setUsuarios(resp)
        console.log(pagRef.current)
    }

    return ({
        usuarios,
        pagSig,
        pagAnt
    })
}
