import { useState, useEffect, useRef } from 'react';
import { Usuario, ReqRespUsers } from '../interfaces/reqResp';
import { reqRespApi } from '../api/reqRes';

export const useUsuarios = () => {
    const [usuarios, setUsuarios] = useState<Usuario[]>([])

    useEffect(() => {
        cargarUsuarios();
    }, [])

    const pagRef = useRef(1);

    const cargarUsuarios = async () => {
        const res = await reqRespApi.get<ReqRespUsers>('/users', {
            params: {
                page: pagRef.current
            }
        })
        setUsuarios(res.data.data)
    }

    const pagSig = () => {
        if (usuarios.length > 0) {
            pagRef.current++;
        } else {
            alert("No hay mas registros")
        }
        cargarUsuarios();
        console.log(pagRef.current)

    }

    const pagAnt = () => {
        if (usuarios.length > 0 && pagRef.current > 1) {
            pagRef.current--;
        } else {
            alert("No hay mas registros atras")
        }
        console.log(pagRef.current)
        cargarUsuarios();
    }

    return ({
        usuarios,
        pagSig,
        pagAnt
    })
}
