import { useState, useEffect, useRef } from 'react';
import { Usuario, ReqRespUsers } from '../interfaces/reqResp';
import { reqRespApi } from '../api/reqRes';

export const useUsuarios = () => {
    const [usuarios, setUsuarios] = useState<Usuario[]>([])

    useEffect(() => {
        cargarUsuarios();
    }, [])

    const pagRef = useRef(1);

    const cargarUsuarios = async () => {
        const res = await reqRespApi.get<ReqRespUsers>('/users', {
            params: {
                page: pagRef.current
            }
        })
        console.log(res.data.data);
        setUsuarios(res.data.data)
    }

    const pagSig = () => {
        pagRef.current++; //2
        cargarUsuarios(); //
        if (usuarios.length == 0) {
            pagRef.current--;
            cargarUsuarios();
            alert('No hay mas usuarios')
        }
        console.log('crrnt', pagRef.current)
        console.log('usrs', usuarios.length)
    }

    const pagAnt = () => {
        if (pagRef.current == 1) {
            alert('No hay anteriores')
            return;
        }
        pagRef.current--;
        cargarUsuarios();
    }

    return ({
        usuarios,
        pagSig,
        pagAnt
    })
}
