import { useEffect, useRef, useState } from 'react';
import { reqRespApi } from '../api/reqRes';
import { ReqRespUsers, Usuario } from '../interfaces/reqResp';

export const Usuarios = () => {

    const [usuarios, setUsuarios] = useState<Usuario[]>([])
    useEffect(() => {
        cargarUsuarios();
    }, [])

    const pagRef = useRef(1);

    const cargarUsuarios = async () => {
        const resp = await reqRespApi.get<ReqRespUsers>('/users', {
            params: {
                page: pagRef.current
            }
        })
        setUsuarios(resp.data.data)
    }

    const renderItem = (usuario: Usuario) => {
        const { first_name, last_name, email, avatar } = usuario;
        return (
            <tr className='text-center' key={usuario.id.toString()}>
                <td> <img style={{ width: 50, borderRadius: 100 }} src={avatar} alt={first_name} /></td>
                <td> {first_name} {last_name}</td>
                <td>{email}</td>
            </tr>
        )
    }

    return (
        <>
            <h1 className="m-3">Usuarios</h1>
            <table className="table">
                <thead>
                    <tr className="text-center">
                        <th>Avatar</th>
                        <th>Nombre</th>
                        <th>Correo </th>

                    </tr>
                </thead>
                <tbody>
                    {
                        usuarios.map(renderItem)
                    }
                </tbody>
            </table>
            <div className='text-center'>
                <button className='btn btn-primary'> <strong>-&gt;</strong></button>
            </div>
        </>
    )
}
